funcSetCoil:
function setFlexCoil(addr, value, unitID) { 
	if (unitID === node.unitId && 
		addr >= node.minAddress && 
		addr <= node.splitAddress) { 

		node.coils.writeUInt8(value, addr * node.bufferFactor)  
	} 
}

